msg.method = 'POST';
msg.url = 'http://localhost:' + context.global.port + '/startTwitterScrape';
msg.payload = {
    onlyWithHashtags: true,
    onlyWithLocation: true,
    maxPlaceSizeMeters: 100,
    boundingBox:{
        latSouth: 30.25,
        latNorth: 30.30,
        lngWest: -97.77,
        lngEast: -97.73
    }
};
return msg;

msg.method = 'POST';
