msg.payload = {
    startDate: '2016-01-03T03:04:00.000Z',
    endDate: '2016-01-05T13:04:00.000Z',
    clusteringDurationMinutes: 4 * 60,
    minTweetsToCluster: 5,
    dbscanEpsilonMeters: 5000,
    dbscanMinMembersInCluster: 4,
